tables.table_schema:
CREATE TABLE ContentMapping (
	id UUID PRIMARY KEY,
	editorial_id UUID,
	celebrity_id UUID
);

CREATE TABLE celebrities (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    age INT,
    profession VARCHAR(50)
);

CREATE TABLE articles (
    id INT PRIMARY KEY,
    title VARCHAR(100),
    author VARCHAR(50),
    publish_date DATE
);

CREATE TABLE images (
    id INT PRIMARY KEY,
    url VARCHAR(255),
    caption TEXT,
    credit VARCHAR(50)
);

CREATE TABLE tags (
    id INT PRIMARY KEY,
    tag_name VARCHAR(50)
);
