tables.table_schema:
CREATE TABLE Celebrities (
  Name VARCHAR(50),
  Event VARCHAR(50),
  Outfit VARCHAR(100)
);

CREATE TABLE Events (
  EventName VARCHAR(50),
  Date DATE,
  Location VARCHAR(50)
);

CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    location VARCHAR(100),
    date DATE
);

CREATE TABLE award_shows (
    id INT PRIMARY KEY,
    celebrity_name VARCHAR(50),
    outfit_description TEXT,
    event_date DATE
);

CREATE TABLE fashion_designers (
    id INT PRIMARY KEY,
    designer_name VARCHAR(50),
    specialty VARCHAR(100)
);

CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50) NOT NULL,
    event_type VARCHAR(50),
    event_date DATE
);

CREATE TABLE Outfits (
    outfit_id INT PRIMARY KEY,
    outfit_name VARCHAR(50),
    designer VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE Ratings (
    rating_id INT PRIMARY KEY,
    outfit_id INT,
    rating DECIMAL(3, 1),
    FOREIGN KEY (outfit_id) REFERENCES Outfits(outfit_id)
);
