tables.table_schema:
CREATE TABLE FreshProduceInventory (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50),
  quantity INT,
  expiration_date DATE
);

CREATE TABLE Alerts (
  alert_id INT PRIMARY KEY,
  product_id INT,
  alert_type VARCHAR(20),
  alert_message VARCHAR(200)
);

CREATE TABLE fresh_produce_inventory (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    quantity INT,
    expiration_date DATE
);

CREATE TABLE low_inventory_alerts (
    id INT PRIMARY KEY,
    product_id INT,
    threshold INT,
    alert_date TIMESTAMP
);

CREATE TABLE expired_inventory_alerts (
    id INT PRIMARY KEY,
    product_id INT,
    expiration_date DATE,
    alert_date TIMESTAMP
);

CREATE TABLE IF NOT EXISTS products (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL,
    expiration_date DATE NOT NULL
);

CREATE TABLE IF NOT EXISTS suppliers (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE IF NOT EXISTS inventory (
    product_id INT REFERENCES products(id),
    supplier_id INT REFERENCES suppliers(id),
    quantity INT NOT NULL,
    purchase_date DATE NOT NULL
);

CREATE TABLE IF NOT EXISTS alerts (
    id INT PRIMARY KEY,
    alert_type VARCHAR(50) NOT NULL,
    alert_message TEXT NOT NULL
);

CREATE TABLE FreshProduceInventory ( 
    product_id INT PRIMARY KEY, 
    product_name VARCHAR(50), 
    quantity INT, 
    expiration_date DATE 
);

CREATE TABLE LowInventoryAlerts ( 
    alert_id INT PRIMARY KEY, 
    product_id INT, 
    alert_time TIMESTAMP, 
    alert_message VARCHAR(255) 
);

CREATE TABLE ExpirationAlerts ( 
    alert_id INT PRIMARY KEY, 
    product_id INT, 
    alert_time TIMESTAMP, 
    alert_message VARCHAR(255) 
);

CREATE TABLE Suppliers ( 
    supplier_id INT PRIMARY KEY, 
    supplier_name VARCHAR(50) 
);
