CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE meal_categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE meal_logs (
    log_id UUID PRIMARY KEY,
    meal_id UUID REFERENCES meals(meal_id) NOT NULL,
    category_id UUID REFERENCES meal_categories(category_id) NOT NULL,
    log_date DATE NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE food_items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(50) NOT NULL,
    calories_per_serving INT NOT NULL
);

CREATE TABLE meal_items (
    meal_id INT REFERENCES meals(meal_id),
    item_id INT REFERENCES food_items(item_id),
    serving_size INT NOT NULL
);

CREATE TABLE trends (
    trend_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    trend_description TEXT NOT NULL,
    trend_date DATE NOT NULL
);
