tables.table_schema:
CREATE TABLE ingredient_inventory (
  id SERIAL PRIMARY KEY,
  ingredient_name VARCHAR(50) NOT NULL,
  current_quantity INT NOT NULL,
  restock_threshold INT NOT NULL
);

CREATE TABLE restock_log (
  log_id SERIAL PRIMARY KEY,
  ingredient_id INT NOT NULL,
  restock_quantity INT NOT NULL,
  restock_date DATE NOT NULL
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    contact_info VARCHAR(100)
);

CREATE TABLE Stockouts (
    stockout_id INT PRIMARY KEY,
    ingredient_id INT,
    stockout_date DATE,
    reason TEXT
);

CREATE TABLE Ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(50),
    current_stock INT,
    unit_of_measure VARCHAR(20)
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(50),
    contact_name VARCHAR(50),
    contact_email VARCHAR(50)
);

CREATE TABLE Stockouts (
    stockout_id INT PRIMARY KEY,
    ingredient_id INT,
    stockout_date DATE,
    FOREIGN KEY (ingredient_id) REFERENCES Ingredients(ingredient_id)
);

CREATE TABLE ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(255) NOT NULL,
    stock_level INT NOT NULL
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL
);

CREATE TABLE stock_history (
    record_id INT PRIMARY KEY,
    ingredient_id INT NOT NULL,
    stock_level INT NOT NULL,
    stock_date TIMESTAMP NOT NULL
);

CREATE TABLE alerts (
    alert_id INT PRIMARY KEY,
    ingredient_id INT NOT NULL,
    alert_level INT NOT NULL,
    alert_message TEXT NOT NULL
);
