tables.table_schema:
CREATE TABLE kitchen_inventory (
    tool_id INT PRIMARY KEY,
    tool_name VARCHAR(50),
    quantity INT,
    location VARCHAR(50)
);

CREATE TABLE kitchen_tools (
    tool_id SERIAL PRIMARY KEY,
    tool_name VARCHAR(50) NOT NULL,
    tool_category VARCHAR(50) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE equipment (
    equipment_id SERIAL PRIMARY KEY,
    equipment_name VARCHAR(50) NOT NULL,
    equipment_type VARCHAR(50) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE KitchenTools (
    tool_id INT PRIMARY KEY,
    tool_name VARCHAR(255),
    quantity INT,
    price DECIMAL(10, 2)
);

CREATE TABLE KitchenEquipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    quantity INT,
    price DECIMAL(10, 2)
);

CREATE TABLE ToolInventory (
    tool_id INT,
    quantity INT,
    FOREIGN KEY (tool_id) REFERENCES KitchenTools(tool_id)
);

CREATE TABLE EquipmentInventory (
    equipment_id INT,
    quantity INT,
    FOREIGN KEY (equipment_id) REFERENCES KitchenEquipment(equipment_id)
);
