tables.table_schema:
CREATE TABLE Meals (
    meal_id UUID PRIMARY KEY,
    meal_name VARCHAR(255),
    meal_description TEXT,
    meal_type VARCHAR(50)
);

CREATE TABLE recipes (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  description TEXT
);

CREATE TABLE recipe_ingredients (
  recipe_id INT,
  ingredient_id INT,
  quantity FLOAT,
  CONSTRAINT pk_recipe_ingredients PRIMARY KEY (recipe_id, ingredient_id)
);

CREATE TABLE meals (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  date DATE
);

CREATE TABLE meals (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL
);

CREATE TABLE meal_plans (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL
);
