tables.table_schema:
CREATE TABLE cocktail_recipes (
    cocktail_id INT PRIMARY KEY,
    cocktail_name VARCHAR(100),
    instructions TEXT
);

CREATE TABLE ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(100),
    quantity VARCHAR(50)
);

CREATE TABLE drinks (
  drink_id INT PRIMARY KEY,
  drink_name VARCHAR(255) NOT NULL,
  drink_category VARCHAR(50),
  instructions TEXT
);

CREATE TABLE ingredients (
  ingredient_id INT PRIMARY KEY,
  ingredient_name VARCHAR(255) NOT NULL,
  ingredient_type VARCHAR(50)
);

CREATE TABLE Recipes (
    recipe_id SERIAL PRIMARY KEY,
    recipe_name TEXT NOT NULL,
    instructions TEXT
);

CREATE TABLE Ingredients (
    ingredient_id SERIAL PRIMARY KEY,
    ingredient_name TEXT NOT NULL,
    quantity DECIMAL
);

CREATE TABLE Cocktails (
    cocktail_id SERIAL PRIMARY KEY,
    cocktail_name TEXT NOT NULL,
    recipe_id INTEGER REFERENCES Recipes(recipe_id),
    instructions TEXT
);

CREATE TABLE CocktailIngredients (
    id SERIAL PRIMARY KEY,
    cocktail_id INTEGER REFERENCES Cocktails(cocktail_id),
    ingredient_id INTEGER REFERENCES Ingredients(ingredient_id),
    quantity DECIMAL
);
