);

CREATE TABLE cocktails (
    cocktail_id INT PRIMARY KEY,
    cocktail_name VARCHAR(50) NOT NULL,
    cocktail_ingredients VARCHAR(200) NOT NULL,
    cocktail_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    customer_email VARCHAR(100) UNIQUE
);

CREATE TABLE cocktails (
    cocktail_id INT PRIMARY KEY,
    cocktail_name VARCHAR(50) NOT NULL,
    cocktail_type VARCHAR(50) NOT NULL,
    cocktail_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    customer_id INT REFERENCES customers(customer_id),
    cocktail_id INT REFERENCES cocktails(cocktail_id),
    order_date DATE NOT NULL
);

CREATE TABLE ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(50) NOT NULL,
    ingredient_type VARCHAR(50) NOT NULL
);

CREATE TABLE order_details (
    order_id INT REFERENCES orders(order_id),
    ingredient_id INT REFERENCES ingredients(ingredient_id),
    quantity INT NOT NULL
);
