CREATE TABLE customer_profiles (
    customer_id UUID PRIMARY KEY,
    name VARCHAR(50),
    favorite_drink VARCHAR(50),
    allergies ARRAY<VARCHAR(50>)
);

CREATE TABLE drinks (
    drink_id UUID PRIMARY KEY,
    drink_name VARCHAR(50)
);

CREATE TABLE allergies (
    allergy_id UUID PRIMARY KEY,
    allergy_name VARCHAR(50)
);

CREATE TABLE drinks (
    drink_id INT PRIMARY KEY,
    drink_name VARCHAR(50),
    description VARCHAR(100)
);

CREATE TABLE allergies (
    allergy_id INT PRIMARY KEY,
    allergy_name VARCHAR(50),
    description VARCHAR(100)
);

CREATE TABLE customer_drinks (
    customer_id INT,
    drink_id INT,
    FOREIGN KEY (customer_id) REFERENCES customer_profiles(customer_id),
    FOREIGN KEY (drink_id) REFERENCES drinks(drink_id)
);

CREATE TABLE allergies (
  allergy_id INT PRIMARY KEY,
  allergy_name VARCHAR(100)
);

CREATE TABLE customer_drinks (
  customer_id INT,
  drink_id INT,
  FOREIGN KEY (customer_id) REFERENCES customer_profiles(customer_id),
  FOREIGN KEY (drink_id) REFERENCES drinks(drink_id)
);

CREATE TABLE customer_allergies (
  customer_id INT,
  allergy_id INT,
  FOREIGN KEY (customer_id) REFERENCES customer_profiles(customer_id),
  FOREIGN KEY (allergy_id) REFERENCES allergies(allergy_id)
);
