tables.table_schema:
CREATE TABLE CocktailPopularity ( 
 cocktail_id INT, 
 cocktail_name VARCHAR(50), 
 popularity_score FLOAT, 
 date_recorded DATE 
);

CREATE TABLE cocktails (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    popularity_score FLOAT
);

CREATE TABLE cocktail_popularity (
    cocktail_id INT,
    date TIMESTAMP,
    popularity_rank INT
);

CREATE TABLE cocktails ( 
	drink_id UUID PRIMARY KEY, 
	drink_name VARCHAR(255), 
	popularity_score INT, 
	created_at TIMESTAMP 
);

CREATE TABLE ingredients ( 
	ingredient_id UUID PRIMARY KEY, 
	ingredient_name VARCHAR(255), 
	category VARCHAR(50) 
);

CREATE TABLE cocktail_logs ( 
	log_id UUID PRIMARY KEY, 
	drink_id UUID, 
	log_message TEXT, 
	logged_at TIMESTAMP 
);

CREATE TABLE cocktails (
    cocktail_id INT PRIMARY KEY,
    cocktail_name VARCHAR(255),
    popularity_rating INT,
    created_date DATE
);

CREATE TABLE ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(255)
);

CREATE TABLE cocktail_ingredients (
    cocktail_id INT,
    ingredient_id INT,
    FOREIGN KEY (cocktail_id) REFERENCES cocktails(cocktail_id),
    FOREIGN KEY (ingredient_id) REFERENCES ingredients(ingredient_id)
);

CREATE TABLE cocktail_history (
    cocktail_id INT,
    popularity_rating INT,
    event_date DATE,
    FOREIGN KEY (cocktail_id) REFERENCES cocktails(cocktail_id)
);
