tables.table_schema:
CREATE TABLE recipe_versions (
    id INT PRIMARY KEY,
    recipe_name TEXT,
    modified_by TEXT,
    modification_timestamp TIMESTAMP
);

CREATE TABLE recipe_history (
    recipe_id INT NOT NULL,
    modification_timestamp TIMESTAMP NOT NULL,
    modification_description VARCHAR(255) NOT NULL,
    PRIMARY KEY (recipe_id, modification_timestamp)
);

CREATE TABLE recipe_modifications (
  modification_id INT PRIMARY KEY,
  recipe_id INT,
  modification_description TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE recipe_tags (
  tag_id INT PRIMARY KEY,
  recipe_id INT,
  tag_name TEXT
);

CREATE TABLE recipe_history (
    recipe_id INT PRIMARY KEY,
    recipe_name VARCHAR,
    modification_timestamp TIMESTAMP
);

CREATE TABLE recipe_ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR
);

CREATE TABLE recipe_steps (
    step_id INT PRIMARY KEY,
    step_description VARCHAR
);

CREATE TABLE recipe_modifiers (
    modifier_id INT PRIMARY KEY,
    modifier_name VARCHAR
);

CREATE TABLE recipe_modification_log (
    log_id INT PRIMARY KEY,
    modified_recipe_id INT,
    modifier_id INT,
    modification_timestamp TIMESTAMP
);
