tables.table_schema:
CREATE TABLE menu ( 
  dish_id SERIAL PRIMARY KEY, 
  dish_name VARCHAR(255) NOT NULL, 
  ingredients TEXT[], 
  price DECIMAL(10, 2) NOT NULL 
);

CREATE TABLE popular_dishes (
    dish_id INT PRIMARY KEY,
    dish_name VARCHAR(255),
    popularity_score INT,
    dish_category VARCHAR(50)
);

CREATE TABLE menu_optimization (
    optimization_id INT PRIMARY KEY,
    dish_id INT,
    discount_amount DECIMAL(10, 2),
    recommended_quantity INT
);

CREATE TABLE dishes (
 dish_id INT PRIMARY KEY,
 dish_name TEXT,
 ingredients TEXT,
 price DECIMAL
);

CREATE TABLE menus (
 menu_id INT PRIMARY KEY,
 menu_name TEXT,
 start_date DATE,
 end_date DATE
);

CREATE TABLE popular_dishes (
 dish_id INT PRIMARY KEY,
 popularity_score DECIMAL,
 CONSTRAINT fk_popular_dishes_dish_id FOREIGN KEY (dish_id) REFERENCES dishes(dish_id)
);
