tables.table_schema:
CREATE TABLE sustainable_farming (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    certification_status BOOLEAN
);

CREATE TABLE farming_practices (
  id INT PRIMARY KEY,
  practice_name VARCHAR(255),
  practice_description TEXT,
  certification_id INT
);

CREATE TABLE certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(255),
  certification_body VARCHAR(255),
  expiration_date DATE
);

CREATE TABLE farm_practices (
    id INT PRIMARY KEY,
    practice_name VARCHAR(255),
    description TEXT,
    certification_id INT
);

CREATE TABLE certifications (
    id INT PRIMARY KEY,
    certification_name VARCHAR(255),
    organization VARCHAR(255),
    validity_period INT
);

CREATE TABLE farm_establishments (
    id INT PRIMARY KEY,
    establishment_name VARCHAR(255),
    location VARCHAR(255),
    certification_id INT
);

CREATE TABLE Farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50),
    location VARCHAR(100),
    certification_status VARCHAR(50)
);

CREATE TABLE Crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    crop_type VARCHAR(50),
    harvest_date DATE
);

CREATE TABLE Certifications (
    certification_id INT PRIMARY KEY,
    certification_name VARCHAR(50),
    certification_authority VARCHAR(100),
    expiration_date DATE
);

CREATE TABLE FarmToTable (
    relationship_id INT PRIMARY KEY,
    farmer_id INT,
    restaurant_id INT,
    relationship_type VARCHAR(50)
);
