tables.table_schema:
CREATE TABLE farm_to_table_deliveries (
    delivery_id SERIAL PRIMARY KEY,
    delivery_date DATE,
    source_location VARCHAR,
    destination_location VARCHAR
);

CREATE TABLE farm_products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    supplier_id INT,
    delivery_date DATE
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    contact_email VARCHAR(255),
    phone_number VARCHAR(15)
);

CREATE TABLE DeliveryRoutes (
    route_id INT PRIMARY KEY,
    origin_address VARCHAR(255),
    destination_address VARCHAR(255),
    delivery_time TIMESTAMP
);

CREATE TABLE DeliveryStatus (
    status_id INT PRIMARY KEY,
    route_id INT REFERENCES DeliveryRoutes(route_id),
    current_status VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE DeliveryLogs (
    log_id INT PRIMARY KEY,
    route_id INT REFERENCES DeliveryRoutes(route_id),
    log_message VARCHAR(255),
    log_time TIMESTAMP
);

CREATE TABLE DeliveryVehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_type VARCHAR(50),
    max_capacity INT,
    current_capacity INT
);

CREATE TABLE delivery_vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_type VARCHAR(50) NOT NULL,
    capacity DECIMAL(8,2) NOT NULL,
    availability BOOLEAN
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    contact_number VARCHAR(15),
    email VARCHAR(50)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    category VARCHAR(50),
    price DECIMAL(8,2) NOT NULL
);
