tables.table_schema:
CREATE TABLE seasonal_trends (
    ingredient_id SERIAL PRIMARY KEY,
    ingredient_name VARCHAR(255),
    availability_date DATE,
    availability_location VARCHAR(255)
);

CREATE TABLE seasonal_ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(50),
    availability_month INT
);

CREATE TABLE menu_plans (
    menu_id INT PRIMARY KEY,
    menu_name VARCHAR(50),
    season VARCHAR(20),
    ingredients_used TEXT
);

CREATE TABLE farm_to_table_ingredients (
   ingredient_id INT PRIMARY KEY,
   ingredient_name VARCHAR(255),
   seasonal_availability VARCHAR(50),
   supplier_id INT
);

CREATE TABLE chefs (
   chef_id INT PRIMARY KEY,
   chef_name VARCHAR(255),
   chef_specialty VARCHAR(100),
   restaurant_id INT
);

CREATE TABLE seasonal_ingredients (
  id INT PRIMARY KEY,
  ingredient_name VARCHAR(255),
  season VARCHAR(50)
);

CREATE TABLE menus (
  menu_id INT PRIMARY KEY,
  menu_name VARCHAR(255),
  menu_date DATE
);

CREATE TABLE recipes (
  recipe_id INT PRIMARY KEY,
  recipe_name VARCHAR(255),
  ingredients TEXT,
  instructions TEXT
);

CREATE TABLE menu_recipes (
  menu_id INT,
  recipe_id INT,
  PRIMARY KEY (menu_id, recipe_id)
);

CREATE TABLE ingredients (
    ingredient_id UUID PRIMARY KEY,
    ingredient_name STRING,
    seasonal_availability STRING
);

CREATE TABLE menus (
    menu_id UUID PRIMARY KEY,
    menu_name STRING,
    prepared_date DATE
);

CREATE TABLE menu_items (
    item_id UUID PRIMARY KEY,
    menu_id UUID REFERENCES menus(menu_id),
    ingredient_id UUID REFERENCES ingredients(ingredient_id)
);

CREATE TABLE recipes (
    recipe_id UUID PRIMARY KEY,
    recipe_name STRING,
    instructions TEXT
);

CREATE TABLE menu_recipes (
    menu_id UUID REFERENCES menus(menu_id),
    recipe_id UUID REFERENCES recipes(recipe_id)
);
