tables.table_schema:
CREATE TABLE origin (
    id UUID PRIMARY KEY,
    country TEXT NOT NULL,
    region TEXT,
    city TEXT
);

CREATE TABLE production_practices (
    id UUID PRIMARY KEY,
    method TEXT NOT NULL,
    certification TEXT,
    description TEXT
);

CREATE TABLE origin_info (
    origin_id INT PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    origin_date DATE
);

CREATE TABLE production_practices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE transportation_info (
    transport_id INT PRIMARY KEY,
    transport_mode VARCHAR(50) NOT NULL,
    distance_km FLOAT
);

CREATE TABLE IF NOT EXISTS origin_info (
    origin_id INT PRIMARY KEY,
    origin_name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL,
    contact_person VARCHAR(255) NOT NULL
);

CREATE TABLE IF NOT EXISTS production_practices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR(255) NOT NULL,
    description TEXT NOT NULL
);

CREATE TABLE origin (
    origin_id INT PRIMARY KEY,
    country VARCHAR(50),
    region VARCHAR(50),
    producer_name VARCHAR(100)
);

CREATE TABLE production_practices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR(100),
