tables.table_schema:
CREATE TABLE FoodWasteData (
    id INT PRIMARY KEY,
    food_item VARCHAR(255),
    quantity INT,
    date_added TIMESTAMP
);

CREATE TABLE food_waste_data (
  id INT PRIMARY KEY,
  food_item VARCHAR(255) NOT NULL,
  waste_amount DECIMAL NOT NULL,
  date_recorded DATE
);

CREATE TABLE waste_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100) NOT NULL,
  category_description TEXT
);

CREATE TABLE waste_reduction_opportunities (
  opportunity_id INT PRIMARY KEY,
  opportunity_name VARCHAR(150) NOT NULL,
  reduction_potential DECIMAL
);

CREATE TABLE FoodWasteData (
   ID INT PRIMARY KEY,
   ProductName VARCHAR,
   Quantity INT,
   Date DATE
);

CREATE TABLE FoodCategories (
   CategoryID INT PRIMARY KEY,
   CategoryName VARCHAR,
   Description VARCHAR
);

CREATE TABLE WasteLocations (
   LocationID INT PRIMARY KEY,
   LocationName VARCHAR,
   Address VARCHAR
);

CREATE TABLE WasteTrends (
   TrendID INT PRIMARY KEY,
   TrendName VARCHAR,
   Frequency VARCHAR,
   Impact VARCHAR
);
