tables.table_schema:
CREATE TABLE packaging_materials (
    material_id UUID PRIMARY KEY,
    material_name VARCHAR(100),
    carbon_footprint DECIMAL,
    recycling_code VARCHAR(10)
);

CREATE TABLE packaging_impact (
    impact_id UUID PRIMARY KEY,
    material_id UUID REFERENCES packaging_materials(material_id),
    impact_description TEXT,
    impact_score DECIMAL
);

CREATE TABLE sustainable_alternatives (
    alternative_id UUID PRIMARY KEY,
    material_id UUID REFERENCES packaging_materials(material_id),
    alternative_name VARCHAR(100),
    alternative_score DECIMAL
);

CREATE TABLE PackagingMaterials (
  packaging_material_id INT PRIMARY KEY,
  material_name VARCHAR(50),
  recyclability VARCHAR(20),
  environmental_impact_score INT
);

CREATE TABLE PackagingManufacturers (
  manufacturer_id INT PRIMARY KEY,
  manufacturer_name VARCHAR(50),
  location VARCHAR(50),
  contact_info VARCHAR(100)
);

CREATE TABLE food_packaging_materials (
    id INT PRIMARY KEY,
    material_name VARCHAR(50),
    impact_rating INT
);

CREATE TABLE packaging_manufacturers (
    id INT PRIMARY KEY,
    manufacturer_name VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE environmental_impact_data (
    id INT PRIMARY KEY,
    material_id INT,
    carbon_footprint DECIMAL,
    water_usage DECIMAL
);

CREATE TABLE packaging_types (
    id INT PRIMARY KEY,
    type_name VARCHAR(50),
    recyclable BOOLEAN
);

CREATE TABLE sustainable_alternatives (
    id INT PRIMARY KEY,
    material_id INT,
    alternative_name VARCHAR(50),
    cost_per_unit DECIMAL
);
