tables.table_schema:
CREATE TABLE FoodMetrics (
  id INT,
  food_name TEXT,
  sustainability_score FLOAT
);

CREATE TABLE ProgressTracking (
  id INT,
  date DATE,
  metric_id INT,
  progress FLOAT
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username STRING,
    email STRING,
    join_date DATE
);

CREATE TABLE Dashboard_Logs (
    log_id UUID PRIMARY KEY,
    user_id UUID,
    activity_description STRING,
    timestamp TIMESTAMP
);

CREATE TABLE progress_tracking (
id INT PRIMARY KEY,
metric_id INT,
value DECIMAL(10, 2),
date_collected DATE
);

CREATE TABLE users (
user_id INT PRIMARY KEY,
username VARCHAR(50),
email VARCHAR(255),
role VARCHAR(20)
);
