tables.table_schema:
CREATE TABLE FoodCompliance (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    sustainability_rating DECIMAL(5,2) NOT NULL
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    supplier_id INT,
    category VARCHAR(50),
    price DECIMAL(10,2),
    CONSTRAINT fk_supplier FOREIGN KEY (supplier_id) REFERENCES Suppliers(supplier_id)
);

CREATE TABLE supplier(
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    supplier_location VARCHAR(255),
    sustainability_rating INT
);

CREATE TABLE distributor(
    distributor_id INT PRIMARY KEY,
    distributor_name VARCHAR(255),
    distributor_location VARCHAR(255),
    compliance_status BOOLEAN
);

CREATE TABLE product(
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    supplier_id INT REFERENCES supplier(supplier_id),
    distributor_id INT REFERENCES distributor(distributor_id),
    quantity INT,
    price DECIMAL(10, 2)
);

CREATE TABLE inventory(
    inventory_id INT PRIMARY KEY,
    product_id INT REFERENCES product(product_id),
    stock_quantity INT
);

CREATE TABLE sustainability_data(
    data_id INT PRIMARY KEY,
    supplier_id INT REFERENCES supplier(supplier_id),
    compliance_date DATE,
    sustainability_notes TEXT
);
