tables.table_schema:
CREATE TABLE allergy_information (
  ingredient_id INT PRIMARY KEY,
  ingredient_name VARCHAR(255) NOT NULL,
  allergen_type VARCHAR(50) NOT NULL,
  allergen_description TEXT
);

CREATE TABLE ingredient_allergies (
  ingredient_id INT PRIMARY KEY,
  allergy_type VARCHAR(50),
  allergy_description TEXT
);

CREATE TABLE ingredient_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50) UNIQUE
);

CREATE TABLE ingredient_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(100),
  source_address TEXT
);

CREATE TABLE ingredient_allergies (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(255) NOT NULL,
    allergy_type VARCHAR(50) NOT NULL
);

CREATE TABLE allergy_types (
    allergy_id INT PRIMARY KEY,
    allergy_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE ingredient_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(100) NOT NULL,
    ingredient_category VARCHAR(50) NOT NULL
);

CREATE TABLE recipes (
    recipe_id INT PRIMARY KEY,
    recipe_name VARCHAR(100) NOT NULL,
    instructions TEXT NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);
