tables.table_schema:
CREATE TABLE wine_varieties (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    color VARCHAR(20),
    region VARCHAR(50)
);

CREATE TABLE grape_varieties (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    region VARCHAR(50),
    color VARCHAR(20)
);

CREATE TABLE wine_regions (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    country VARCHAR(50),
    climate VARCHAR(50)
);

CREATE TABLE wine_producers (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    region_id INT,
    FOREIGN KEY (region_id) REFERENCES wine_regions(id)
);
