tables.table_schema:
CREATE TABLE wine_selections (
    id SERIAL PRIMARY KEY,
    wine_name VARCHAR(255),
    grape_variety VARCHAR(255),
    region VARCHAR(255)
);

CREATE TABLE wine_events (
    event_id INT PRIMARY KEY,
    venue_name VARCHAR(255) NOT NULL,
    event_date DATE NOT NULL,
    participant_name VARCHAR(255) NOT NULL
);

CREATE TABLE participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    participant_email VARCHAR(255)
);

CREATE TABLE Venue (
    venue_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Event (
    event_id INT PRIMARY KEY,
    date DATE NOT NULL,
    venue_id INT,
    FOREIGN KEY (venue_id) REFERENCES Venue(venue_id)
);

CREATE TABLE Participant (
    participant_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255)
);

CREATE TABLE Events (
  event_id UUID PRIMARY KEY,
  venue TEXT NOT NULL,
  event_date DATE NOT NULL
);

CREATE TABLE Participants (
  participant_id UUID PRIMARY KEY,
  participant_name TEXT NOT NULL,
  event_id UUID REFERENCES Events(event_id)
);

CREATE TABLE Wines (
  wine_id UUID PRIMARY KEY,
  wine_name TEXT NOT NULL,
  wine_type TEXT
);

CREATE TABLE Event_Participants (
  event_id UUID REFERENCES Events(event_id),
  participant_id UUID REFERENCES Participants(participant_id)
);

CREATE TABLE Event_Wines (
  event_id UUID REFERENCES Events(event_id),
  wine_id UUID REFERENCES Wines(wine_id)
);
