tables.table_schema:
CREATE TABLE Winemakers (
    winemaker_id INT PRIMARY KEY,
    winemaker_name TEXT,
    winery_name TEXT
);

CREATE TABLE TastingEvents (
    event_id INT PRIMARY KEY,
    event_name TEXT,
    event_date DATE,
    event_location TEXT
);

CREATE TABLE Tasters (
    taster_id INT PRIMARY KEY,
    taster_name TEXT
);
