tables.table_schema:
CREATE TABLE ingredient_substitutions (
    substitution_id INT PRIMARY KEY,
    original_ingredient VARCHAR(255),
    substituted_ingredient VARCHAR(255),
    substitution_reason TEXT
);

CREATE TABLE Ingredient (ingredient_id SERIAL PRIMARY KEY,
              name VARCHAR(50) NOT NULL,
              category VARCHAR(50) NOT NULL);

CREATE TABLE Recipe (recipe_id SERIAL PRIMARY KEY,
              name VARCHAR(100) NOT NULL,
              description TEXT);
