tables.table_schema:
CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    age INT,
    email VARCHAR(50)
);

CREATE TABLE dishes (
    dish_id INT PRIMARY KEY,
    dish_name VARCHAR(50),
    cuisine_type VARCHAR(30),
    ingredients TEXT
);

CREATE TABLE quality_improvement (
    improvement_id INT PRIMARY KEY,
    dish_id INT,
    improvement_area TEXT,
    suggested_changes TEXT
);

CREATE TABLE customers_feedback (
    customer_id INT,
    dish_id INT,
    rating FLOAT,
    feedback TEXT
);

CREATE TABLE dishes (
    dish_id INT,
    dish_name VARCHAR(255),
    cuisine_type VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE ratings (
    rating_id INT,
    dish_id INT,
    customer_id INT,
    overall_rating FLOAT
);

CREATE TABLE cuisines (
    cuisine_id INT,
    cuisine_name VARCHAR(100),
    region VARCHAR(50)
);

CREATE TABLE regions (
    region_id INT,
    region_name VARCHAR(50)
);
