tables.table_schema:
CREATE TABLE cooking_techniques (
  technique_id INT PRIMARY KEY,
  technique_name VARCHAR(50) NOT NULL,
  popularity_score DECIMAL(5,2),
  user_feedback TEXT
);

CREATE TABLE cooking_techniques ( 
    technique_id INT PRIMARY KEY, 
    technique_name VARCHAR(50) NOT NULL, 
    popularity_score FLOAT NOT NULL 
);

CREATE TABLE user_interactions ( 
    user_id INT PRIMARY KEY, 
    technique_id INT NOT NULL, 
    interaction_type VARCHAR(20) NOT NULL, 
    interaction_timestamp TIMESTAMP 
);

CREATE TABLE Techniques (
    technique_id SERIAL PRIMARY KEY,
    technique_name VARCHAR(50) NOT NULL,
    popularity_score INT DEFAULT 0
);

CREATE TABLE Users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE Interactions (
    interaction_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES Users (user_id),
    interaction_type VARCHAR(50) NOT NULL,
    interaction_timestamp TIMESTAMP
);

CREATE TABLE Feedback (
    feedback_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES Users (user_id),
    technique_id INT REFERENCES Techniques (technique_id),
    feedback_text TEXT,
    feedback_rating INT
);

CREATE TABLE TechniquePopularity (
    technique_id INT REFERENCES Techniques (technique_id),
    total_interactions INT DEFAULT 0,
    average_rating NUMERIC(3, 2) DEFAULT 0.00
);
