tables.table_schema:
CREATE TABLE ingredients (
    ingredient_id UUID PRIMARY KEY,
    name VARCHAR(255),
    quantity INT,
    unit VARCHAR(50)
);

CREATE TABLE recipes (
    recipe_id UUID PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    preparation_time INT
);

CREATE TABLE shopping_list (
    list_id UUID PRIMARY KEY,
    name VARCHAR(255),
    created_at TIMESTAMP,
    completed BOOLEAN
);
