  recipe_id INT PRIMARY KEY,
  recipe_name VARCHAR,
  cooking_time_minutes INT,
  difficulty_level VARCHAR
);

CREATE TABLE user_recipe (
  user_id INT,
  recipe_id INT,
  FOREIGN KEY(user_id) REFERENCES user(user_id),
  FOREIGN KEY(recipe_id) REFERENCES recipe(recipe_id)
);

