tables.table_schema:
CREATE TABLE demographics (
    lead_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(50)
);

CREATE TABLE interests (
    interest_id INT PRIMARY KEY,
    lead_id INT,
    interest_name VARCHAR(50)
);

CREATE TABLE behavior (
    behavior_id INT PRIMARY KEY,
    lead_id INT,
    purchase_count INT,
    latest_interaction TIMESTAMP
);

CREATE TABLE LeadInterests (
    lead_id INT PRIMARY KEY,
    hobby VARCHAR(255),
    interest_category VARCHAR(255)
);

CREATE TABLE LeadBehavior (
    lead_id INT PRIMARY KEY,
    behavior_type VARCHAR(255),
    interaction_timestamp TIMESTAMP,
    interaction_detail VARCHAR(255)
);

CREATE TABLE MarketingCampaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255) NOT NULL,
    start_date DATE,
    end_date DATE
);
