tables.table_schema:
CREATE TABLE retention_strategy (
  id INT PRIMARY KEY,
  strategy_name VARCHAR(255),
  test_group VARCHAR(50),
  conversion_rate DECIMAL
);

CREATE TABLE retention_strategy (
   strategy_id INT PRIMARY KEY,
   strategy_name VARCHAR(255) NOT NULL,
   test_group INT NOT NULL
);

CREATE TABLE retention_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255) NOT NULL,
    experiment_start_date DATE NOT NULL,
    experiment_end_date DATE NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE experiments (
    experiment_id INT PRIMARY KEY,
    experiment_name VARCHAR(255) NOT NULL,
    experiment_description TEXT
);

CREATE TABLE experiment_results (
    result_id INT PRIMARY KEY,
    experiment_id INT NOT NULL,
    user_id INT NOT NULL,
    conversion_rate FLOAT NOT NULL
);

CREATE TABLE user_segments (
    segment_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    segment_name VARCHAR(255) NOT NULL
);
