tables.table_schema:
CREATE TABLE conversion_rates (
    id INT PRIMARY KEY,
    stage_name VARCHAR(255),
    conversion_rate DECIMAL(10,2)
);

CREATE TABLE sales_funnel_stages (
    stage_id INT PRIMARY KEY,
    stage_name VARCHAR(255),
    funnel_position INT,
    goal_conversion_rate DECIMAL(10,2)
);

CREATE TABLE stage_visits (
    visit_id UUID PRIMARY KEY,
    stage_name VARCHAR(50),
    visit_timestamp TIMESTAMP
);

CREATE TABLE conversions (
    conversion_id UUID PRIMARY KEY,
    visit_id UUID REFERENCES stage_visits(visit_id),
    converted_at TIMESTAMP
);

CREATE TABLE bottlenecks (
    bottleneck_id UUID PRIMARY KEY,
    stage_name VARCHAR(50),
    bottleneck_description TEXT
);

CREATE TABLE sales_funnel_stage_1 (
    user_id INT,
    landing_page_visited BOOLEAN,
    conversion_event_time TIMESTAMP
);

CREATE TABLE sales_funnel_stage_2 (
    user_id INT,
    email_capture_successful BOOLEAN,
    capture_event_time TIMESTAMP
);

CREATE TABLE sales_funnel_stage_3 (
    user_id INT,
    product_page_visited BOOLEAN,
    page_visited_event_time TIMESTAMP
);

CREATE TABLE sales_funnel_stage_4 (
    user_id INT,
    add_to_cart_successful BOOLEAN,
    cart_event_time TIMESTAMP
);

CREATE TABLE sales_funnel_stage_5 (
    user_id INT,
    purchase_completed BOOLEAN,
    purchase_event_time TIMESTAMP
);
