    followup_status VARCHAR(20)
);

CREATE TABLE LeadStatus (
    lead_id INT,
    lead_status VARCHAR(20)
);

CREATE TABLE Criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(255),
    criteria_description TEXT
);

CREATE TABLE FollowUpActions (
    action_id INT PRIMARY KEY,
    action_name VARCHAR(255),
    action_description TEXT,
    action_type VARCHAR(50)
);

CREATE TABLE LeadCriteria (
    lead_id INT,
    criteria_id INT,
    CONSTRAINT fk_lead_id FOREIGN KEY (lead_id) REFERENCES Leads(lead_id),
    CONSTRAINT fk_criteria_id FOREIGN KEY (criteria_id) REFERENCES Criteria(criteria_id)
);

CREATE TABLE CriteriaActions (
    criteria_id INT,
    action_id INT,
    CONSTRAINT fk_criteria_id FOREIGN KEY (criteria_id) REFERENCES Criteria(criteria_id),
    CONSTRAINT fk_action_id FOREIGN KEY (action_id) REFERENCES FollowUpActions(action_id)
);
