tables.table_schema:
CREATE TABLE reports (
 report_id UUID PRIMARY KEY,
 lead_conversion_rate FLOAT,
 sales_pipeline TEXT,
 ROI DECIMAL
);

CREATE TABLE lead_conversion_rates (
    lead_id UUID PRIMARY KEY,
    converted BOOLEAN,
    conversion_date DATE
);

CREATE TABLE lead_conversion_rates (id INT PRIMARY KEY,
lead_id INT,
conversion_rate FLOAT,
conversion_date DATE
);

CREATE TABLE roi_analysis (id INT PRIMARY KEY,
lead_id INT,
roi FLOAT,
roi_date DATE
);

CREATE TABLE lead_conversion_rates (
    lead_id INT PRIMARY KEY,
    conversion_status VARCHAR,
    conversion_date DATE,
    lead_source VARCHAR
);

CREATE TABLE sales_pipeline (
    deal_id INT PRIMARY KEY,
    deal_value DECIMAL,
    deal_stage VARCHAR,
    closing_date DATE
);

CREATE TABLE sql_data_analysis (
    analysis_id INT PRIMARY KEY,
    analysis_date DATE,
    analyst_name VARCHAR,
    report_type VARCHAR
);

CREATE TABLE lead_conversion (
    lead_id INT PRIMARY KEY,
    lead_name VARCHAR(255),
    conversion_status VARCHAR(50),
    conversion_date DATE
);
