tables.table_schema:
CREATE TABLE user_interests (
    user_id INT,
    interest_id INT,
    interest_name VARCHAR(50),
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE ads (
    ad_id INT PRIMARY KEY,
    ad_content TEXT NOT NULL,
    ad_target VARCHAR(50) NOT NULL,
    ad_cost DECIMAL(10, 2)
);
