tables.table_schema:
CREATE TABLE content_engagement_metrics (
    content_id INT PRIMARY KEY,
    likes INT,
    shares INT,
    comments INT
);

CREATE TABLE Content (
    id INT PRIMARY KEY,
    title VARCHAR,
    body TEXT,
    publication_date DATE
);

CREATE TABLE likes (
    id INT PRIMARY KEY,
    post_id INT,
    user_id INT
);

CREATE TABLE shares (
    id INT PRIMARY KEY,
    post_id INT,
    user_id INT
);

CREATE TABLE comments (
    id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    comment_text TEXT
);

CREATE TABLE Likes { 
	like_id UUID PRIMARY KEY, 
	content_id UUID, 
	user_id UUID, 
	like_date TIMESTAMP 
}
