tables.table_schema:
CREATE TABLE trending_topics (
    id INT PRIMARY KEY,
    topic VARCHAR(255),
    hashtag VARCHAR(50),
    activity_count INT
);

CREATE TABLE trending_topics (
    id INT PRIMARY KEY,
    topic VARCHAR(255),
    popularity INT
);

CREATE TABLE user_activity (
    user_id INT PRIMARY KEY,
    activity_date DATE,
    hashtag VARCHAR(50)
);

CREATE TABLE UserActivity (
    user_id INT PRIMARY KEY,
    activity_timestamp TIMESTAMP,
    activity_type VARCHAR(255),
    activity_details TEXT
);

CREATE TABLE TrendingTopics (
    topic_id SERIAL PRIMARY KEY,
    topic_name VARCHAR(255) UNIQUE,
    total_mentions INT
);

CREATE TABLE UserHashtag (
    user_id INT,
    hashtag VARCHAR(50),
    PRIMARY KEY(user_id, hashtag)
);

CREATE TABLE trending_topics (
  topic_id INT PRIMARY KEY,
  topic_title VARCHAR(100) NOT NULL,
  topic_description TEXT
);

CREATE TABLE popular_hashtags (
  hashtag_id INT PRIMARY KEY,
  hashtag_text VARCHAR(50) NOT NULL,
  hashtag_count INT
);

CREATE TABLE user_activity (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  activity_timestamp TIMESTAMP NOT NULL,
  activity_type VARCHAR(20)
);

CREATE TABLE topic_hashtags (
  topic_id INT,
  hashtag_id INT,
  FOREIGN KEY (topic_id) REFERENCES trending_topics(topic_id),
  FOREIGN KEY (hashtag_id) REFERENCES popular_hashtags(hashtag_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    post_content TEXT NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE likes (
    like_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    post_id INT NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE post_hashtags (
    post_id INT NOT NULL,
    hashtag_id INT NOT NULL,
    PRIMARY KEY (post_id, hashtag_id)
);
