tables.table_schema:
CREATE TABLE user_content (
    id INT PRIMARY KEY,
    user_id INT,
    content TEXT,
    created_at TIMESTAMP
);

CREATE TABLE user_content (
    user_id UUID PRIMARY KEY,
    content TEXT
);

CREATE TABLE comments (
    comment_id UUID PRIMARY KEY,
    user_id UUID,
    content TEXT,
    FOREIGN KEY (user_id) REFERENCES user_content(user_id) ON DELETE CASCADE
);

CREATE TABLE UserProfiles (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    bio TEXT
);

CREATE TABLE UserPosts (
    post_id INT PRIMARY KEY,
    user_id INT,
    content TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    created_at TIMESTAMP
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    join_date TIMESTAMP
);

CREATE TABLE Posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    post_content TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE Likes (
    like_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    like_time TIMESTAMP
);

CREATE TABLE Followers (
    follower_id INT PRIMARY KEY,
    user_id INT,
    follower_user_id INT
);
