tables.table_schema:
CREATE TABLE email_triggers (
    trigger_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    trigger_type VARCHAR(50) NOT NULL,
    trigger_date TIMESTAMP
);

CREATE TABLE welcome_emails (
    user_id INT PRIMARY KEY,
    subject VARCHAR(255),
    body TEXT,
    sent_at TIMESTAMP
);

CREATE TABLE follow_up_emails (
    user_id INT PRIMARY KEY,
    subject VARCHAR(255),
    body TEXT,
    sent_at TIMESTAMP
);

CREATE TABLE user_actions (
    user_id INT PRIMARY KEY,
    action_type VARCHAR(50),
    action_date TIMESTAMP
);
