CREATE TABLE subscribers (
 subscriber_id INT PRIMARY KEY,
 subscriber_name VARCHAR(50),
 email_address VARCHAR(100),
 timezone VARCHAR(50)
);

CREATE TABLE subscribers (
    subscriber_id INT PRIMARY KEY,
    email_address VARCHAR(255) NOT NULL,
    timezone VARCHAR(50) NOT NULL
);

CREATE TABLE emails (
    email_id INT PRIMARY KEY,
    subject VARCHAR(255) NOT NULL,
    content TEXT NOT NULL
);

CREATE TABLE time_zones (
    timezone_id INT PRIMARY KEY,
    time_zone_name VARCHAR(50) NOT NULL
);

CREATE TABLE optimal_send_times (
    time_id INT PRIMARY KEY,
    time_slot TIME NOT NULL,
    timezone_id INT NOT NULL,
    FOREIGN KEY (timezone_id) REFERENCES time_zones (timezone_id)
);

CREATE TABLE subscribers (
    subscriber_id INT PRIMARY KEY,
    email VARCHAR(255),
    location VARCHAR(100),
    timezone VARCHAR(50)
);

CREATE TABLE email_schedules (
    schedule_id INT PRIMARY KEY,
    subscriber_id INT,
    send_time TIMESTAMP,
    FOREIGN KEY (subscriber_id) REFERENCES subscribers(subscriber_id)
);

CREATE TABLE time_zones (
    timezone_id INT PRIMARY KEY,
    timezone_name VARCHAR(50),
    offset_hours INT
);

CREATE TABLE optimal_send_times (
    time_zone_id INT,
    day_of_week VARCHAR(15),
    optimal_send_time TIME,
    FOREIGN KEY (time_zone_id) REFERENCES time_zones(timezone_id)
);
