tables.table_schema:
CREATE TABLE customer_demographics (
    customer_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR(50),
    location VARCHAR(100),
    satisfaction_score INT
);

CREATE TABLE satisfaction_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(100),
    pattern_description TEXT
);

CREATE TABLE customers(
    customer_id INT PRIMARY KEY,
    name VARCHAR,
    age INT,
    gender VARCHAR
);

CREATE TABLE demographics(
    demo_id INT PRIMARY KEY,
    customer_id INT,
    location VARCHAR,
    income INT
);

CREATE TABLE satisfaction_scores(
    score_id INT PRIMARY KEY,
    customer_id INT,
    score INT,
    feedback VARCHAR
);

CREATE TABLE patterns(
    pattern_id INT PRIMARY KEY,
    customer_id INT,
    pattern_type VARCHAR
);

CREATE TABLE locations (
  location_id INT,
  location_name VARCHAR(50),
  region VARCHAR(50),
  country VARCHAR(50)
);

CREATE TABLE surveys (
  survey_id INT,
  survey_name VARCHAR(50),
  start_date DATE,
  end_date DATE
);
