tables.table_schema:
CREATE TABLE survey_results (
    survey_id INT PRIMARY KEY,
    customer_id INT,
    dissatisfaction_reason TEXT
);

CREATE TABLE dissatisfied_customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    contact_info TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    customer_email VARCHAR
);

CREATE TABLE surveys (
    survey_id INT PRIMARY KEY,
    survey_date DATE,
    satisfaction_score INT
);

CREATE TABLE survey_answers (
    answer_id INT PRIMARY KEY,
    survey_id INT,
    customer_id INT,
    answer_text VARCHAR
);

CREATE TABLE survey_responses (
    response_id INT PRIMARY KEY,
    customer_id INT,
    satisfaction_rating INT,
    reason_for_dissatisfaction TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    email TEXT,
    phone_number TEXT
);

CREATE TABLE dissatisfaction_reasons (
    reason_id INT PRIMARY KEY,
    reason_description TEXT
);

CREATE TABLE survey_questions (
    question_id INT PRIMARY KEY,
    question_text TEXT
);

CREATE TABLE customer_survey (
    survey_id INT PRIMARY KEY,
    customer_id INT,
    satisfaction_rating INT,
    reason_for_dissatisfaction TEXT
);

CREATE TABLE survey_question (
    question_id INT PRIMARY KEY,
    question_text TEXT
);

CREATE TABLE customer_feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT
);

CREATE TABLE dissatisfaction_reason (
    reason_id INT PRIMARY KEY,
    reason_name TEXT
);
