    feedback TEXT
);

CREATE TABLE NPSData (
    nps_id INT PRIMARY KEY,
    customer_id INT,
    nps_score INT,
    nps_category TEXT
);

CREATE TABLE survey_responses (
  id INT PRIMARY KEY,
  customer_id INT,
  question TEXT,
  score INT
);

CREATE TABLE customers (
  id INT PRIMARY KEY,
  name TEXT,
  email TEXT
);

CREATE TABLE nps_scores (
  id INT PRIMARY KEY,
  nps_score INT,
  survey_id INT,
  FOREIGN KEY (survey_id) REFERENCES survey_responses(id)
);

CREATE TABLE survey_responses (
    response_id int PRIMARY KEY,
    user_id int,
    survey_score int,
    comment text
);

CREATE TABLE users (
    user_id int PRIMARY KEY,
    email text,
    name text
);

CREATE TABLE surveys (
    survey_id int PRIMARY KEY,
    survey_date date,
    survey_type text
);

CREATE TABLE nps_scores (
    nps_id int PRIMARY KEY,
    survey_id int,
    nps_score int
);

CREATE TABLE nps_categories (
    category_id int PRIMARY KEY,
    nps_score int,
    category_name text
);
