tables.table_schema:
CREATE TABLE LeadStatus (
    lead_id UUID PRIMARY KEY,
    lead_name VARCHAR(50) NOT NULL,
    lead_status VARCHAR(20) NOT NULL,
    update_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE LeadStatus (
    lead_id INT PRIMARY KEY,
    lead_name VARCHAR(255),
    lead_status VARCHAR(50)
);

CREATE TABLE leads (
    lead_id INT PRIMARY KEY,
    lead_name VARCHAR(255),
    lead_status VARCHAR(50),
    lead_source VARCHAR(50)
);

CREATE TABLE lead_updates (
    update_id INT PRIMARY KEY,
    lead_id INT,
    update_timestamp TIMESTAMP,
    update_description VARCHAR(255)
);

CREATE TABLE lead_tracking (
  lead_id INT,
  lead_name TEXT,
  lead_status TEXT,
  last_updated TIMESTAMP
);

CREATE TABLE lead_users (
  user_id INT,
  username TEXT,
  role TEXT
);
