tables.table_schema:
CREATE TABLE ad_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE ad_variants (
    variant_id INT PRIMARY KEY,
    creative_id INT,
    variant_name VARCHAR(255),
    variant_description TEXT
);

CREATE TABLE ad_creative (
  id UUID PRIMARY KEY,
  creative_name VARCHAR,
  campaign_id UUID,
  variation_id UUID
);

CREATE TABLE campaign (
  id UUID PRIMARY KEY,
  campaign_name VARCHAR,
  start_date DATE,
  end_date DATE
);

CREATE TABLE ad_placement (
  id UUID PRIMARY KEY,
  placement_name VARCHAR,
  cost DECIMAL,
  impressions INT
);

CREATE TABLE ad_variant (
  id UUID PRIMARY KEY,
  variant_name VARCHAR,
  creative_id UUID,
  is_winner BOOLEAN
);
