tables.table_schema:
CREATE TABLE marketing_insights (
   id INT PRIMARY KEY,
   department_name VARCHAR(255),
   data_insight TEXT
);

CREATE TABLE department_alignment (
   id INT PRIMARY KEY,
   department_name VARCHAR(255),
   align_strategy TEXT
);

CREATE TABLE MarketingData (
    id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    data_insights TEXT,
    department_id INT
);

CREATE TABLE Department (
    id INT PRIMARY KEY,
    department_name VARCHAR(255),
    department_head VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE DataSharing (
    id INT PRIMARY KEY,
    department_id INT,
    table_name VARCHAR(255),
    shared_insights TEXT
);

CREATE TABLE department { 
  department_id INT PRIMARY KEY, 
  department_name VARCHAR(50) NOT NULL 
};

CREATE TABLE employee { 
  employee_id INT PRIMARY KEY, 
  employee_name VARCHAR(50) NOT NULL, 
  department_id INT NOT NULL, 
  manager_id INT, 
  FOREIGN KEY (department_id) REFERENCES department(department_id), 
  FOREIGN KEY (manager_id) REFERENCES employee(employee_id) 
};

CREATE TABLE marketing_insights { 
  insight_id INT PRIMARY KEY, 
  insight_description TEXT NOT NULL, 
  employee_id INT NOT NULL, 
  created_at TIMESTAMP NOT NULL, 
  FOREIGN KEY (employee_id) REFERENCES employee(employee_id) 
};

CREATE TABLE strategy_alignment { 
  alignment_id INT PRIMARY KEY, 
  alignment_description TEXT NOT NULL, 
  insight_id INT NOT NULL, 
  approved BOOLEAN NOT NULL, 
  FOREIGN KEY (insight_id) REFERENCES marketing_insights(insight_id) 
};

CREATE TABLE department_data (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(50) NOT NULL,
  data_insights TEXT
);

CREATE TABLE marketing_data (
  marketing_id INT PRIMARY KEY,
  marketing_campaign VARCHAR(50) NOT NULL,
  target_audience VARCHAR(50) NOT NULL,
  budget DECIMAL(10, 2) NOT NULL
);

CREATE TABLE collaboration_logs (
  log_id INT PRIMARY KEY,
  department_id INT NOT NULL,
  marketing_id INT NOT NULL,
  collaboration_date DATE NOT NULL
);

CREATE TABLE department_reports (
  report_id INT PRIMARY KEY,
  department_id INT NOT NULL,
  report_date DATE NOT NULL,
  insights TEXT
);

CREATE TABLE marketing_reports (
  report_id INT PRIMARY KEY,
  marketing_id INT NOT NULL,
  report_date DATE NOT NULL,
  insights TEXT
);
