tables.table_schema:
CREATE TABLE buyer_personas (
    persona_id INT PRIMARY KEY,
    persona_name VARCHAR(50),
    persona_description TEXT
);

CREATE TABLE lead_personas (
    lead_persona_id INT PRIMARY KEY,
    lead_id INT REFERENCES leads(lead_id),
    persona_id INT REFERENCES buyer_personas(persona_id)
);

CREATE TABLE BuyerPersonas (
    persona_id INT PRIMARY KEY,
    persona_name VARCHAR(50),
    persona_description TEXT
);

CREATE TABLE Segmentations (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(50),
    segment_criteria TEXT
);

CREATE TABLE CommunicationStrategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    strategy_description TEXT
);
