tables.table_schema:
CREATE TABLE SurveyFeedback ( 
  survey_id INT PRIMARY KEY, 
  customer_id INT, 
  feedback_text TEXT, 
  rating INT 
);

CREATE TABLE PersonalizedPromotions ( 
  promotion_id INT PRIMARY KEY, 
  customer_id INT, 
  promotion_description TEXT, 
  discount_amount DECIMAL(10, 2) 
);

CREATE TABLE survey_feedback (
    survey_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text VARCHAR(255),
    submission_date TIMESTAMP
);

CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    preferred_promotion_id INT,
    notification_preference BOOLEAN,
    last_updated TIMESTAMP
);

CREATE TABLE CustomerSurvey (
    survey_id INT PRIMARY KEY,
    customer_id INT,
    survey_date TIMESTAMP,
    survey_responses JSON
);

CREATE TABLE Promotion (
    promotion_id INT PRIMARY KEY,
    promotion_name TEXT,
    start_date TIMESTAMP,
    end_date TIMESTAMP
);

CREATE TABLE CustomerPromotion (
    customer_id INT,
    promotion_id INT,
    promotion_discount FLOAT,
    PRIMARY KEY (customer_id, promotion_id)
);
