tables.table_schema:
CREATE TABLE social_media_engagement (
    customer_id INT PRIMARY KEY,
    social_media_platform VARCHAR(255),
    engagement_level VARCHAR(50),
    last_interaction_date DATE
);

CREATE TABLE customers (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE engagements (
    engagement_id INT PRIMARY KEY,
    customer_id INT,
    channel VARCHAR(50),
    engagement_time TIMESTAMP
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name TEXT,
  customer_email TEXT,
  customer_phone TEXT
);

CREATE TABLE social_media_channels (
  channel_id INT PRIMARY KEY,
  channel_name TEXT,
  channel_type TEXT
);

CREATE TABLE social_media_platforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50),
    description TEXT
);

CREATE TABLE engagement_history (
    history_id INT PRIMARY KEY,
    interaction_id INT,
    engagement_details TEXT
);
